import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import HomeScreen from './Screen/HomeScreen';
import AppDetailsScreen from './Screen/AppDetailsScreen';
import { Text, Image } from 'react-native'; // Importa Text desde 'react-native'

const Stack = createStackNavigator();

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen
          name="Home"
          component={HomeScreen}
          options={{
            headerTitle: () => (
              <Text style={{ fontSize: 24, fontWeight: 'bold' }}>Mi Aplicaci√≥n</Text>
            ),
          }}
        />
        <Stack.Screen name="AppDetails" component={AppDetailsScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default App;
