// src/screens/HomeScreen.js
import React, { useState } from 'react';
import { View, FlatList, Text } from 'react-native';
import { gamesData } from '../data/gamesData';
import SearchBar from '../components/SearchBar';
import AppList from '../components/AppList'; 

const HomeScreen = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [showSearchResults, setShowSearchResults] = useState(false);

  const handleSearch = (searchText) => {
    // Filtrar los juegos según el texto de búsqueda
    const filteredGames = gamesData.filter((game) =>
      game.name.toLowerCase().includes(searchText.toLowerCase())
    );
    setSearchResults(filteredGames);
    setShowSearchResults(true);
  };

  return (
    <View>
      <SearchBar onSearch={handleSearch} />
      {showSearchResults ? (
        searchResults.length === 0 ? (
          <Text>No se encontraron juegos</Text>
        ) : (
          <AppList data={searchResults} /> // Usa AppList o el nombre de tu componente de lista de juegos
        )
      ) : (
        <AppList data={gamesData} /> // Usa AppList o el nombre de tu componente de lista de juegos
      )}
    </View>
  );
};

export default HomeScreen;
