import React from 'react';
import { View, Text, Image, ScrollView, Button, StyleSheet } from 'react-native';

const AppDetails = ({ app }) => {
  // Funci√≥n para mostrar una plataforma con un emoji
  const renderPlatform = (platform) => {
    let emoji = '';
    switch (platform) {
      case 'iOS':
        emoji = 'üçé';
        break;
      case 'Android':
        emoji = 'ü§ñ';
        break;
      // Agrega m√°s plataformas y emojis seg√∫n sea necesario
      default:
        emoji = '';
    }
    return (
      <Text style={styles.platform}>
        {emoji} {platform}
      </Text>
    );
  };

  return (
    <ScrollView style={styles.container}>
      <Image source={{ uri: app.screenshots[0] }} style={styles.image} />

      <View style={styles.detailsContainer}>
        <Text style={styles.name}>{app.name}</Text>
        <Text style={styles.description}>{app.description}</Text>
        <Text style={styles.category}>Categor√≠a: {app.category}</Text>
        <Text style={styles.rating}>Calificaci√≥n: {app.rating}</Text>

        {/* Mostrar las plataformas */}
        <View style={styles.platformsContainer}>
          {app.platforms.map((platform, index) => (
            <View key={index}>{renderPlatform(platform)}</View>
          ))}
        </View>

        {/* Agregar m√°s detalles aqu√≠, por ejemplo, detalles sobre el desarrollador, tama√±o del archivo, etc. */}
      </View>

      {/* Agregar botones u opciones de descarga aqu√≠ */}
      <View style={styles.downloadContainer}>
        <Button title="Descargar" onPress={() => handleDownload(app)} />
        {/* Agregar m√°s botones u opciones de descarga seg√∫n sea necesario */}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  image: {
    aspectRatio: 16 / 9, // Establece la relaci√≥n de aspecto (ejemplo: 16:9)
    width: '100%',
    alignSelf: 'center', // Centra horizontalmente la imagen
    marginBottom: 16,
  },
  detailsContainer: {
    marginBottom: 16,
  },
  name: {
    fontSize: 24,
    fontWeight: 'bold',
  },
  description: {
    fontSize: 16,
    marginBottom: 8,
  },
  category: {
    fontSize: 16,
    color: '#666',
  },
  rating: {
    fontSize: 16,
    color: '#666',
    marginBottom: 16,
  },
  platformsContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 8,
  },
  platform: {
    fontSize: 16,
    marginRight: 8,
  },
  downloadContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
  },
  // Agrega m√°s estilos seg√∫n sea necesario
});

export default AppDetails;
